import classes from "./HeaderCartButton.module.css";
import CartIcon from "../Cart/Carticon";
import { useContext } from "react";
import CartContext from "../../Shop/cart-context";
const Cartbutton = (props) => {
  const ctx=useContext(CartContext)
  const totalitemamount=ctx.items.reduce((currvalue,items)=>{
    return currvalue + items.amount
  },0)
  
  return (
    <button className={classes.button} onClick={props.cartbuttonhandler}>
      <span className={classes.icon}>
        <CartIcon />
      </span>
      <span>Your Cart</span>
      <span className={classes.badge}>{totalitemamount}</span>
    </button>
  );
};
export default Cartbutton
