import { useReducer } from "react";
import CartContext from "./cart-context";
const initalvalue={
    items:[],
    totalAmount:0
}
const cartreducer=(state,action)=>{
    if(action.type === 'ADD'){
        const arritems=state.items.concat(action.items)
        const totalAmount=state.totalAmount + action.item.price * action.item.amount
        return {
            items:arritems, totalAmount
        }
    }
    return initalvalue
}

const Cartprovider = (props) => {
    const [cart,dispatchcart]=useReducer(cartreducer,initalvalue)
  const additemToCartHandler = (item) => {
    dispatchcart({
        type:'ADD', items:item
    })
  };
  const removeitemToCartHandler = (id) => {};
  const cartcontext = {
    items: [],
    totalamount: 0,
    additem: additemToCartHandler,
    removeitem: removeitemToCartHandler,
  };
  return (
    <CartContext.Provider value={cartcontext}>
      {props.children}
    </CartContext.Provider>
  );
};
export default Cartprovider;
