import classes from "./Cart.module.css";
import ModalOverlay from "../UI/Modal";
import { useContext } from "react";
import CartContext from "../../Shop/cart-context";
import CartItem from "./CartItem";
const Cart = (props) => {
  const cartctx=useContext(CartContext)
  const cartitems = (
    <ul className={classes["cart-items"]}>
      {cartctx.items.map((items) => (
        <CartItem amount={items.amount} price={items.price} name={items.name} onRemove={cartctx.removeitem}  />
      ))}
    </ul>
  );
  return (
    <ModalOverlay hidecart={props.carthandler}>
        {cartitems}
      <div className={classes["cart-items"]}>
        <div className={classes.total}>
          <span>Total Amount</span>
          <span>${cartctx.totalamount}</span>
        </div>
        <div className={classes.actions}>
          <button className={classes["button--alt"]} onClick={props.carthandler}>Close</button>
          <button className={classes.button}>Order</button>
        </div>
      </div>
    </ModalOverlay>
  );
};
export default Cart;
